import { BrowserRouter, Route, Switch } from "react-router-dom";
import { useContext } from "react";
import { ThemeContext } from "./../scripts/Theme";


const Top_sidebar = () => {

    const {theme, toggleTheme} = useContext(ThemeContext)

    return (
        <div class={theme ? "top-sidebar" : "top-sidebar-light"}>
            <button title={theme ? "Mode clair" : "Mode sombre"} onClick={toggleTheme}>
                <i className={theme ? "fas fa-sun" : "fas fa-moon"}></i>
            </button>
            <button title="Plan du site">
            <BrowserRouter>
                <Switch>
                    <Route path="/" exact component={Home} />
                    {/* <a href="/plan">
                        <i className="fas fa-list-ol"></i>
                    </a> */}
                </Switch>
            </BrowserRouter>
            </button>
            <button title="Crédits"><i className="fas fa-handshake"></i></button>
        </div>
    );
};

export default Top_sidebar;
